import React, { Component } from 'react';
require('dotenv').config();

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      data: [],
      isLoaded: false,
      steamId: process.env.REACT_APP_STEAM_ID,
      error: null,
      currentId: process.env.REACT_APP_STEAM_ID
    }
  }

  componentDidMount() {
    fetch(`IPlayerService/GetOwnedGames/v0001/?key=${process.env.REACT_APP_API_KEY}&steamid=${this.state.steamId}&format=json&include_appinfo=true`,
      {
        "headers":
        {
          "Accept": "application.json"
        }
      })
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          data: json.response
        })
      })
      .catch(function (error) {
        console.log('Error: ',
          error.message);
        this.setState({
          isLoaded: true,
          data: null
        })
      });
  }

  handleChange(e) {
    this.setState({ steamId: e });
  }

  handleClick() {
    this.setState({ currentId: this.state.steamId })
    this.componentDidMount();
  }

  render() {
    var { isLoaded, data, steamId } = this.state;

    if (!isLoaded) {
      return <h2> Loading Data, Please Wait... </h2>
    }
    else if (data.game_count) {
      return (
        <div className="App">
          <h1>Game List for ID: {steamId}</h1>
          <input
            type="text"
            value={this.state.currentId}
            onChange={e => this.handleChange(e.target.value)}
          />
          <input
            className="btn"
            type="button"
            value="Submit"
            onClick={e => this.handleClick(e.target.value)}
          />
          <h2>Game Count: {data.game_count}</h2>
          <ul>
            {data.games.map(game => (
              <li key={game.appid}>
                <strong>{game.name}:</strong> {Math.floor(game.playtime_forever / 60)} hours, {Math.round((game.playtime_forever / 60 - Math.floor(game.playtime_forever / 60)) * 60)}minutes.
            </li>
            ))};
          </ul>
        </div>
      );
    }
    else if (data == null) {
      return <h2> Issue Connecting to Steam API Service... (Steam Response Empty) </h2>
    }
    else {
      this.setState({
        data: null
      })
      return <h2> Invalid Input </h2>
    }
  }
}

export default App;
