import React, { Component } from 'react';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      data: [],
      isLoaded: false,
    }
  }

  componentDidMount() {

    fetch(`/IPlayerService/GetOwnedGames/v0001/?key=8BC89686A32E04B9D481E19A374608B6&steamid=76561198042185172&format=json`,
      {
        "headers":
        {
          "Accept": "application.json"
        }
      })
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          data: json.response
        })
      })
      .catch(err => {
        console.log(err)
      });
  }

  render() {
    var { isLoaded, data } = this.state;

    if (!isLoaded) {
      return <div> Loading Data... </div>
    }
    else {
      return (
        <div className="App">
          <h1>Game List for ID: 76561198042185172</h1>
          <h2>Game Count: {data.game_count}</h2>
          <ul>
          {data.games.map(game => (
            <li key= {game.appid}>
              Game ID: {game.appid} | Gametime Played: {game.playtime_forever % 60} hours, {game.playtime_forever - game.playtime_forever % 60} minutes
            </li>
          ))};

          </ul>
        </div>
      );
    }
  }
}

export default App;
