import React, { Component } from 'react';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      dataItems: [],
      isLoaded: false,
    }
  }

  componentDidMount() {

    fetch(`api.steampowered.com/IPlayerService/GetOwnedGames/v0001/?key=76561198042185172&steamid=8BC89686A32E04B9D481E19A374608B6&format=json`)
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          dataItems: json,
        })
      })
      .catch(err => {
        console.log(err)
      });
  }

  Render() {

    var { isLoaded, dataItems } = this.state;

    if (!isLoaded) {
      return <div> Loading Data... </div>
    }
    else {
      return (
        <div> className="App"

        </div>
      );
    }  
  }
}

export default App;
