import React, { Component } from 'react';
require('dotenv').config();

function getURL( steamId ) {
  return `/IPlayerService/GetOwnedGames/v0001/?key=${process.env.REACT_APP_API_KEY}&steamid=${steamId}&format=json&include_appinfo=true`
}


class App extends Component {

  handleChange(value) {    
    this.setState({
      steamId: value
    });
  }

  constructor(props) {
    super(props);
    this.state = {
      data: [],
      isLoaded: false,
      steamId: process.env.REACT_APP_STEAM_ID
    }
  }

  componentDidMount() {
    fetch(getURL( this.state.steamId ),
      {
        "headers":
        {
          "Accept": "application.json"
        }
      })
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          data: json.response
        })
      })
      .catch(err => {
        console.log(err)
      });
  }

  render() {
    var { isLoaded, data } = this.state;
    if (!isLoaded) {
      return <h2> Loading Data... </h2>
    }
    else if (data.game_count) {
      return (
        <div className="App">
          <input
            type="text"
            value={this.state.steamId}
            onChange={e => this.handleChange(e.target.value)}
          />
          <button onClick={this.componentDidMount()}>Submit</button>
          <h1>Game List for ID: {process.env.REACT_APP_STEAM_ID}</h1>
          <h2>Game Count: {data.game_count}</h2>
          <ul>
            {data.games.map(game => (
              <li key={game.appid}>
                <strong>{game.name}:</strong> {Math.floor(game.playtime_forever / 60)} hours, {Math.round((game.playtime_forever / 60 - Math.floor(game.playtime_forever / 60)) * 60)}minutes.
            </li>
            ))};
        </ul>
        </div>
      );
    }
    else {
      return <h2> Issue Connecting to Steam API Service... </h2>
    }
  }
}

export default App;
